var app = angular.module('Chefonia', ["ngRoute", "ngCookies"]);

app.config(['$routeProvider',
    function ($routeProvider, $locationProvider) {

        $routeProvider.when('/about-us', {
            templateUrl: 'foodkart/pages/about-us/about-us.html'
        }).when('/how-it-works', {
            templateUrl: 'foodkart/pages/how-we-work/how-it-works.html'
        }).when('/contact-us', {
            templateUrl: 'foodkart/pages/contact-us/contact-us.html'
        }).when('/register', {
            templateUrl: 'pages/register/register.html',
            controller: 'RegisterController',
            controllerAs: 'vm'
        }).when('/login', {
            templateUrl: 'pages/login/login.html',
            controller: 'LoginController',
            controllerAs: 'vm'
        }).when('/address', {
            templateUrl: 'pages/address/address.html'
        }).when('/payment', {
            templateUrl: 'pages/payment/payment.html'
        }).when('/cart', {
            templateUrl: 'pages/cart/cartdetails.html',
            controller: 'CartController',
            controllerAs: 'vm'
        }).when('/checkout', {
            templateUrl: 'pages/checkout/checkout.html',
            controller: 'CheckoutController',
            controllerAs: 'vm'
        }).when('/item', {
            templateUrl: 'pages/items/items.html',
            controller: 'ItemController',
            controllerAs: 'vm'
        }).when('/orderDetail/:orderId', {
            templateUrl: 'pages/orderSummary/orderSummary.html',
            controller: 'OrderSummaryController',
            controllerAs: 'vm'
        }).when('/success', {
            templateUrl: 'pages/success-order/success.html',
        }).when('/login_mble', {
            templateUrl: 'pages/login/login_mble.html',
            controller: 'ItemController',
            controllerAs: 'vm'
        }).when('/orders', {
            templateUrl: 'pages/orders/orders.html',
            controller: 'OrdersController',
            controllerAs: 'vm'
        }).otherwise({
            templateUrl: 'pages/front/front.html',
            controller: 'FrontController',
            controllerAs: 'vm'
        });
    }]).run(function ($rootScope, SessionService, $location) {
    $rootScope.$on("$routeChangeStart", function (event, next, current) {
        var isLoggedIn = SessionService.get(SessionService.Session.CurrentUser) != undefined;
        if (!isLoggedIn) {
            $location.path('home');
        }
    })

});

app.controller('RC', function ($rootScope, $scope, $http) {
    $rootScope.homepage = true;
    $scope.chef = {};
    $scope.displayMsg = "";
    $scope.formSubmitted = false;
    $scope.submit = function () {
        $scope.formSubmitted = true;
        $scope.displayMsg = "Your request successfully submitted."
        var req = {
            method: 'POST',
            url: 'http://chefonia.com/endpoint/db',
            headers: {
                'Content-Type': 'application/json;charset=UTF-8',
                'Authorization': 'Basic ' + "c2VjcmV0YWRtaW46cGFzc3dvcmRAY2hlZm9uaWFmb29ka2FydA=="
            },
            data: $scope.chef
        };
        try {
            $http(req).then(function (response) {
                $scope.chef = {};
                if (response.status == 201) {
                    $scope.displayMsg = "Successfully registered. We will call you soon."
                } else if (response.status == 202) {
                    $scope.displayMsg = "You are already registered with us."
                } else {
                    $scope.displayMsg = "Oops something went wrong, please try later.";
                }
            });
        } catch (err) {
            $scope.displayMsg = "Oops something went wrong, please try later.";
        }


    }

    $scope.reset = function () {
        $scope.formSubmitted = false;
        $scope.displayMsg = "";
        $scope.chef = {};
    }
    $scope.reset = function () {
        $scope.formSubmitted = false;
        $scope.displayMsg = "";
        $scope.chef = {};
    }
    animateHeader();
});

var animateHeader = function () {
    $('.heading').textillate({
        minDisplayTime: 5000,
        loop: true,
        autoStart: true,
        in: {
            effect: 'fadeInUp',
            delayScale: 1.5,
            delay: 50,
            sync: false,
            sequence: true
        },
        out: {
            effect: 'fadeInUp',
            delayScale: 1.5,
            delay: 50,
            sync: false,
            sequence: true
        }
    });
};

